import React, { useState } from "react";
import { Button, Img, Line, Text } from "components";

function StudentProfile() {
  const [isMenuOpen, setMenuOpen] = useState(false);

  const toggleMenu = () => {
    setMenuOpen(!isMenuOpen);
  };

  return (
    <div className="relative cursor-pointer">
      <div className="flex bg-slate-900 font-poppins h-full w-full mb-[50%] ">
        <div className="absolute flex flex-col items-center justify-start left-1 w-[100%]">
          <div className="flex flex-col items-end justify-end md:ml-[0] pl-1 py-1 w-full">
            <Text
              className="mt-4 sm:text-[12px] md:text-[15px] text-[15px] text-blue_gray-900"
              size="txtPoppinsBold25"
            >
              Jane Doe
            </Text>
          </div>

          <Text
            className="absolute flex flex-row items-center justify-start top-9 ml-[94%] text-teal-900 text-l"
            size="txtPoppinsMedium20"
          >
            Student
          </Text>
        </div>
        <div>
          <Img
            className="absolute top-5 h-10 left-[91%] rounded-[50%] w-10"
            src="images/img_ellipse2711.png"
            alt="ellipse2711"
          />
        </div>
      </div>

      {/* Menu */}
      {isMenuOpen && (
        <div className="absolute bg-white border border-gray-300 rounded shadow-lg p-4 bottom-0 left-0">
          <Button className="mb-2">Logout</Button>
          <Button className="mb-2">Sign In</Button>
          <Button>Settings</Button>
        </div>
      )}

      {/* Clickable area to toggle the menu */}
      <div
        className="absolute top-0 left-0 right-0 bottom-0 cursor-pointer"
        onClick={toggleMenu}
      >
        {/* You can place an icon or any clickable element here */}
        <Img
          className="top-10 left-700px rounded-[50%] w-10 h-10"
          src="images/img_ellipse2711.png"
          alt="ellipse2711"
        />
      </div>
    </div>
  );
}

export default StudentProfile;
