import React from "react";
import { Menu, MenuItem, Sidebar, useProSidebar } from "react-pro-sidebar";
import { Img, Text } from "components";

const Sidebar1 = (props) => {
  const { collapseSidebar, collapsed } = useProSidebar();

  // Function to toggle visibility of the logo based on sidebar collapse state
  const toggleLogoVisibility = () => {
    const logoElements = document.querySelectorAll('.umat-logo');
    if (logoElements) {
      logoElements.forEach((element) => {
        element.classList.toggle('hidden', collapsed);
      });
    }
  };

  return (
    <Sidebar
      collapsed={collapsed}
      onClick={() => collapseSidebar(!collapsed)}
      className={`umat-logo ${props.className}`}
      style={{ transition: "width 0.5s" }} // Add transition for smoother animation
    >
      {/* Sidebar content */}
      <div className="sidebar-content">
        {/* Logo */}
        <Img
          className={`cursor-pointer umat-logo md:h-auto ${collapsed ? "hidden" : ""} mt-[5%] ml-[85%] mr-2.5 object-cover w-[29px]`}
          src="images/left-arrow.png"
          alt="lessthan"
        />
        <Menu
          menuItemStyles={{
            button: {
              padding: "0 5%",
              gap: "12px",
              margin: "0 0 10%",
              color: "#ffffff",
              fontWeight: 700,
              fontSize: "30px",
              fontFamily: "Inika",
            },
          }}
          className="umat-logo flex items-center justify-start mt-[10%] w-full"
        >
          <MenuItem
            icon={
              <Img
                className="umat-logo common-pointer h-[39px] md:h-auto inset-auto object-cover relative w-full"
                src="images/img_umatlogo1.png"
                alt="umatlogoOne"
                onClick={props?.onClick}
              />
            }
          >
            <Text className="umat-logo md:text-2xl sm:text-[28px] text-shadow-ts">
              UMaT - TSAP
            </Text>
          </MenuItem>
        </Menu>

        {/* Menu items */}
        <div className="flex flex-col justify-center items-center">
          {/* Render the menu icon only when the sidebar is collapsed */}
          {collapsed && (
            <Img
              className="cursor-pointer mb-[50%] h-10 w-10"
              src="images/menu-burger-new.png"
              alt="menu-icon"
            />
          )}
          {/* Other menu items */}
          {!collapsed && (
            <>
              <Img className="mb-[50%] h-10 w-10" src="images/img_home.svg" alt="home" />
              {/* Add other menu items here */}
            </>
          )}
        </div>

        {/* Text menu items */}
        {!collapsed && (
          <div className="umat-logo pl-[10%]">
            <Text className="h-10 pt-[8%] mb-[12%] flex text-lg text-white-A700" size="txtPoppinsMedium18">
              Home
            </Text>
            {/* Add other text menu items here */}
          </div>
        )}
      </div>
    </Sidebar>
  );
};

export default Sidebar1;
