import React, { useState } from "react";
import MainDashboardTop from "pages/Login/MainDashboardTop";

function HostelPages() {

  const [currentView, setCurrentView] = useState("Overview");

  const handleButtonClick = (view) => {
    setCurrentView(view);
  };


  return (
    <div className="relative">
      <MainDashboardTop />
      <div className="md:flex mb-12 ml-[10%] mr-[10%]">
      <div className="w-[100%] h-96 md:w-[60%] h-[100%] mb-10 mr-5">
          {" "}
          <img
            src="images/hostel1.jpeg"
            className="ml-[5%] w-[90%] h-96 md:w-[100%] gallery-image rounded-2xl md:ml-0 "
          />
        </div>
      <div className="grid grid-cols-2 ml-2 mr-2 md:m-0">
        
        <div className="w-[90%] md:w-[100%] h-96 gallery-image">
          <img
            src="images/hostel2.jpeg"
            className="w-[100%] object-cover h-60 md:w-[90%] rounded-2xl mb-10 gallery-image"
          />
          <img src="images/hostel3.jpeg" className="w-[90%]  md:w-[90%] h-32 md:h-40 rounded-2xl gallery-image" />
        </div>
        <div className="w-[90%] md:w-[100%] h-96 gallery-image">
          
          <img src="images/hostel3.jpeg" className="w-[90%]  md:w-[90%] h-32 md:h-40 rounded-2xl mb-10 gallery-image" />
          <img
            src="images/hostel2.jpeg"
            className="w-[100%] object-cover h-60 md:w-[90%] rounded-2xl"
          />
        </div>
      </div>
      
    </div>
    <div className="login flex justify-around p-10 mb-10">
        <button onClick={() => handleButtonClick("Overview")}><p>Overview</p></button>
        <button onClick={() => handleButtonClick("Rooms")}><p>Rooms</p></button>
        <button onClick={() => handleButtonClick("Location")}><p>Location</p></button>
        <button onClick={() => handleButtonClick("Amenities")}><p>Amenities</p></button>
      </div>

      <div>
        {currentView === "Overview" && (
          <div>
            <h2 className="bottom-headings mb-2">Hostel Overview</h2>
            <p className="login2 mb-2">Overview content goes here...</p>
          </div>
        )}

        {currentView === "Rooms" && (
          <div>
            <h2 className="bottom-headings mb-2">Hostel Rooms</h2>
            <p className="login2 mb-2">Rooms information goes here...</p>
          </div>
        )}

        {currentView === "Location" && (
          <div>
            <h2 className="bottom-headings mb-2">Hostel Location</h2>
            <p className="login2 mb-2">Location information goes here...</p>
          </div>
        )}

        {currentView === "Amenities" && (
          <div>
            <h2 className="bottom-headings mb-2">Hostel Amenities</h2>
            <p className="login2 mb-2">Amenities information goes here...</p>
          </div>
        )}
      </div>
 

   </div>
  );
}

export default HostelPages;
