import React, { useState } from "react";
import HostelList from "./HostelList";
import EmailCard from "./EmailCard";
import SelectionCheckBox from "./SelectionCheckBox";
import MainDashboard from "./MainDashboard";
import NewFooter from "./Footer";

function Login() {
  // State to manage dropdown visibility
 

  const [focusedInput, setFocusedInput] = useState(null);

  const handleFocus = (inputIndex) => {
    setFocusedInput(inputIndex);
  };

  const handleBlur = () => {
    setFocusedInput(null);
  };


  const [currentView, setCurrentView] = useState("Overview");

  const handleButtonClick = (view) => {
    setCurrentView(view);
    setActiveButton(view); // Update active button when clicked
  };

  return (
    <div className="overall-container">
      <MainDashboard />
      <div className="md:flex w-full justify-center w-full mt-20 pl-[10%] pr-[10%] mb-20">
        <SelectionCheckBox />
        <HostelList />
      </div>

      {/* <EmailCard /> */}

      <NewFooter />
    </div>
  );
}

export default Login;
