import React, { useEffect } from "react";
import { Menu, MenuItem, Sidebar, useProSidebar } from "react-pro-sidebar";
import { Img, Text } from "components";

const Sidebar1 = (props) => {
  const { collapseSidebar, collapsed } = useProSidebar();

  // Collapse sidebar by default
  useEffect(() => {
    collapseSidebar(true);
  }, []);

  const handleToggleSidebar = () => {
    // Only toggle the sidebar if the viewport is medium or larger
    if (window.innerWidth >= 768) {
      collapseSidebar(!collapsed);
    }
  };

  // Listen for window resize events to adjust sidebar behavior
  useEffect(() => {
    const handleResize = () => {
      if (window.innerWidth < 768) {
        collapseSidebar(true);
      } else {
        // Expand sidebar if viewport is medium or larger
        collapseSidebar(false);
      }
    };

    window.addEventListener("resize", handleResize);
    return () => window.removeEventListener("resize", handleResize);
  }, [collapseSidebar]);

  return (
    <>
      <div className="" style={{ height: "170vh" }}>
        <Sidebar
          onClick={handleToggleSidebar}
          className={`umat-logo ${props.className}`}
          collapsed={collapsed} // Set collapsed state of Sidebar component
        >
          <div className="sidebar-content">
            <Img
              className="cursor-pointer umat-logo md:h-auto mt-[5%] ml-[85%] mr-2.5 object-cover w-[29px] "
              src="images/left-arrow.png"
              alt="lessthan"
            />
            <Menu
              menuItemStyles={{
                button: {
                  padding: "0 5%",
                  gap: "12px",
                  margin: "0 0 10%",
                  color: "#ffffff",
                  fontWeight: 700,
                  fontSize: "30px",
                  fontFamily: "Inika",
                },
              }}
              className="umat-logo flex items-center justify-start mt-[10%] w-full"
            >
              <MenuItem
                icon={
                  <Img
                    className="umat-logo common-pointer h-[39px] md:h-auto inset-auto object-cover relative w-full "
                    src="images/img_umatlogo1.png"
                    alt="umatlogoOne"
                    onClick={props?.onClick}
                  />
                }
              >
                <Text className="umat-logo md:text-2xl sm:text-[28px] text-shadow-ts">
                  UMaT - TSAP
                </Text>
              </MenuItem>
            </Menu>

            {/* Sidebar content here */}
          </div>
        </Sidebar>
      </div>
    </>
  );
};

export default Sidebar1;
