import React, { useEffect } from "react";
import { Menu, MenuItem, Sidebar, useProSidebar } from "react-pro-sidebar";
import { Img, Text } from "components";

const Sidebar1 = (props) => {
  const { collapseSidebar, collapsed } = useProSidebar();

  useEffect(() => {
    // Add or remove the 'umat-logo' id to all children when collapsed state changes
    const sidebarContent = document.getElementById('sidebar-content');
    if (sidebarContent) {
      const children = sidebarContent.children;
      for (let i = 0; i < children.length; i++) {
        const child = children[i];
        if (collapsed) {
          child.id = 'umat-logo';
        } else {
          child.id = ''; // Remove the id when not collapsed
        }
      }
    }
  }, [collapsed]);

  return (
    <>
      <Sidebar
        onClick={() => collapseSidebar(!collapsed)}
        className={props.className}
      >
        <div id="sidebar-content">
          <Img
            className="md:h-auto md-[0] mt-[5%] ml-[85%] mr-2.5 object-cover w-[29px] "
            src="images/img_lessthan.png"
            alt="lessthan"
          />
          <Menu
            menuItemStyles={{
              button: {
                padding: "0 5% 5%",
                gap: "16px",
                margin: "0 0 10%",
                color: "#ffffff",
                fontWeight: 700,
                fontSize: "32px",
                fontFamily: "Inika",
              },
            }}
            className="flex flex-col items-center justify-start mb-[1039px] mt-[11px] w-full"
          >
            <MenuItem
              icon={
                <Img
                  className="common-pointer h-[39px] md:h-auto inset-auto object-cover relative w-full "
                  src="images/img_umatlogo1.png"
                  alt="umatlogoOne"
                  onClick={props?.onClick}
                />
              }
            >
              <Text className="md:text-2xl sm:text-[28px] text-shadow-ts">
                UMaT - TSAP
              </Text>
            </MenuItem>
          </Menu>
        </div>
      </Sidebar>
    </>
  );
};

export default Sidebar1;
