import React, { useState, useEffect } from "react";
import { Menu, MenuItem, Sidebar, useProSidebar } from "react-pro-sidebar";
import { Img, Text } from "components";

const Sidebar1 = (props) => {
  const { collapseSidebar, collapsed } = useProSidebar();
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    // Function to handle viewport resizing
    const handleResize = () => {
      setIsMobile(window.innerWidth <= 576); // Check if viewport is smaller than or equal to "sm"
    };

    // Add event listener for window resize
    window.addEventListener("resize", handleResize);

    // Call handleResize to set initial state
    handleResize();

    // Clean up event listener on component unmount
    return () => {
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  useEffect(() => {
    // Function to toggle logo visibility based on sidebar collapse state
    const toggleLogoVisibility = () => {
      const logoElements = document.querySelectorAll(".umat-logo");
      if (logoElements) {
        logoElements.forEach((element) => {
          element.classList.toggle("hidden", collapsed);
        });
      }
    };

    // Call toggleLogoVisibility whenever sidebar collapse state changes
    toggleLogoVisibility();
  }, [collapsed]);

  useEffect(() => {
    // Function to toggle logo visibility based on sidebar collapse state
    const toggleLogoVisibility = () => {
      if (window.innerWidth <= 576) { // Check if viewport is smaller than or equal to "sm"
        const logoElements = document.querySelectorAll(".umat-logo");
        if (logoElements) {
          logoElements.forEach((element) => {
            element.classList.toggle("hidden", collapsed);
          });
        }
      }
    };

    // Call toggleLogoVisibility whenever sidebar collapse state changes
    toggleLogoVisibility();
  }, [collapsed]);


  if (isMobile) {
    // If viewport is smaller than or equal to "sm", hide the sidebar completely
    return null;
  }



  return (
    <>
      <div className="left-[0] h-full">
        <Sidebar
          onClick={() => collapseSidebar(!collapsed)}
          className={`umat-logo ${props.className}`}
        >
          <div className="">
            <Img
              className="hidden cursor-pointer umat-logo md:h-auto mt-[5%] ml-[85%] object-cover w-[29px] "
              src="images/left-arrow.png"
              alt="lessthan"
            />
            <Menu
              menuItemStyles={{
                button: {
                  padding: "0 5%",
                  gap: "12px",
                  margin: "0 0 10%",
                  color: "#ffffff",
                  fontWeight: 700,
                  fontSize: "30px",
                  fontFamily: "Inika",
                },
              }}
              className="umat-logo flex items-center justify-start mt-[10%] w-full"
            >
              <MenuItem
                icon={
                  <Img
                    className="hidden umat-logo common-pointer h-[20px] md:h-[29px] inset-auto object-cover relative w-full"
                    src="images/img_umatlogo1.png"
                    alt="umatlogoOne"
                    onClick={props?.onClick}
                  />
                }
              >
                <Text className=" umat-logo text-[25px] text-shadow-ts">
                  UMaT - TSAP
                </Text>
              </MenuItem>
            </Menu>

            <div className="pb-[10%] flex justify-center">
              <div>
                <div className="">
                  {collapsed && (
                    <div className="">
                      {/* Render the menu icon only when the sidebar is collapsed */}
                      <Img
                        className="cursor-pointer mb-[50%] h-10 w-10"
                        src="images/menu-burger-new.png"
                        alt="menu-icon"
                      />
                    </div>
                  )}
                </div>

                <Img
                  className="mb-[50%] h-10 w-10"
                  src="images/img_home.svg"
                  alt="home"
                />
                <Img
                  className="mb-[50%] h-10 w-10"
                  src="images/img_thumbsup.svg"
                  alt="thumbsup"
                />
                <Img
                  className="mb-[50%] h-10 w-10"
                  src="images/img_student.svg"
                  alt="student_One"
                />
                <Img
                  className="mb-[50%] h-10 w-10"
                  src="images/img_thumbsup_blue_gray_200.svg"
                  alt="thumbsup_One"
                />
                <Img
                  className="mb-[50%] h-10 w-10"
                  src="images/img_finance.svg"
                  alt="finance"
                />
                <Img
                  className="mb-[50%] h-10 w-10"
                  src="images/img_calendar.svg"
                  alt="calendar"
                />
              </div>

              <div className="hidden umat-logo pl-[10%] ">
                <Text
                  className="h-10 pt-[8%] mb-[12%] flex text-lg text-white-A700"
                  size="txtPoppinsMedium18"
                >
                  Home
                </Text>
                <Text
                  className="h-10 pt-[8%] mb-[12%] text-lg text-white-A700"
                  size="txtPoppinsMedium18"
                >
                  Submissions
                </Text>
                <Text
                  className="h-10 pt-[8%] mb-[12%] text-lg text-white-A700"
                  size="txtPoppinsMedium18"
                >
                  Report
                </Text>
                <Text
                  className="h-10 pt-[8%] mb-[12%] text-lg text-white-A700"
                  size="txtPoppinsMedium18"
                >
                  Event
                </Text>
                <Text
                  className="h-10 pt-[8%] mb-[12%] text-lg text-white-A700"
                  size="txtPoppinsMedium18"
                >
                  Assignments
                </Text>
                <Text
                  className="h-10 pt-[8%] text-lg text-white-A700"
                  size="txtPoppinsMedium18"
                >
                  Announcements
                </Text>
              </div>
            </div>
          </div>
        </Sidebar>
      </div>
    </>
  );
};

export default Sidebar1;
