import React, { useState } from "react";
import { Link, useNavigate } from "react-router-dom";

function Login() {
  const navigate = useNavigate();
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const [error, setError] = useState("");

  const handleUsernameChange = (event) => {
    setUsername(event.target.value);
  };

  const handlePasswordChange = (event) => {
    setPassword(event.target.value);
  };

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      // Send a POST request to your authentication endpoint with the username and password
      const response = await fetch("https://portal.umat.edu.gh/mainapi/api/UserAccount/login", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ username, password }),
      });
      const data = await response.json();
        console.log(data)

      if (response.ok) {
        // If authentication is successful, navigate to the next page
        navigate("/FrameOne");
      } else {
        // If authentication fails, display an error message
        setError("Invalid username or password");

      }
    } catch (error) {
      console.error("Error authenticating user:", error);
    }
  };

  return (
    <div className="loginContainer bg-white-A700 shadow-xl w-[30%] m-auto h-[400px] text-center mt-[10%]">
      <form onSubmit={handleSubmit}>
        <div className="pt-[5%]">
          <h1>UMaT TSAP</h1>
          <div className="inputField">
            <div className="m-[5%]">
              <input
                type="text"
                placeholder="Username"
                value={username}
                onChange={handleUsernameChange}
              />
            </div>
            <div className="m-[5%]">
              <input
                type="password"
                placeholder="Password"
                value={password}
                onChange={handlePasswordChange}
              />
            </div>
          </div>
          <button
            type="submit"
            className="bg-teal-700 hover:bg-blue-700 text-white font-bold py-2 px-20 rounded-full"
          >
            Login
          </button>
          {error && <p className="text-red-500">{error}</p>}
        </div>
      </form>
    </div>
  );
}

export default Login;
