import React, { useEffect, useState } from "react";
import { Menu, MenuItem, Sidebar, useProSidebar } from "react-pro-sidebar";
import { Img, Text } from "components";

const Sidebar1 = (props) => {
  const { collapsed } = useProSidebar();
  const [isSmallViewport, setIsSmallViewport] = useState(false);

  useEffect(() => {
    const handleResize = () => {
      setIsSmallViewport(window.innerWidth < 768); // 768 is the breakpoint for sm viewport
    };

    window.addEventListener("resize", handleResize);
    handleResize(); // Check viewport size initially
    return () => window.removeEventListener("resize", handleResize);
  }, []);

  return (
    <>
      <div className="" style={{ height: "170vh" }}>
        <Sidebar
          collapsed={isSmallViewport} // Set collapsed state based on viewport size
          className={`umat-logo ${props.className}`}
        >
          <div className="sidebar-content">
            {/* Code for the sidebar content */}
            {isSmallViewport ? (
              // Sidebar content for small viewport (collapsed)
              <div className="">
                <Img
                  className="cursor-pointer mb-[50%] h-10 w-10"
                  src="images/menu-burger-new.png"
                  alt="menu-icon"
                />
              </div>
            ) : (
              // Sidebar content for medium to xl viewport (expanded)
              <>
                <Img
                  className="cursor-pointer umat-logo md:h-auto mt-[5%] ml-[85%] mr-2.5 object-cover w-[29px] "
                  src="images/left-arrow.png"
                  alt="lessthan"
                />
                <Menu
                  menuItemStyles={{
                    button: {
                      padding: "0 5%",
                      gap: "12px",
                      margin: "0 0 10%",
                      color: "#ffffff",
                      fontWeight: 700,
                      fontSize: "30px",
                      fontFamily: "Inika",
                    },
                  }}
                  className="umat-logo flex items-center justify-start mt-[10%] w-full"
                >
                  <MenuItem
                    icon={
                      <Img
                        className="umat-logo common-pointer h-[39px] md:h-auto inset-auto object-cover relative w-full "
                        src="images/img_umatlogo1.png"
                        alt="umatlogoOne"
                        onClick={props?.onClick}
                      />
                    }
                  >
                    <Text className="umat-logo md:text-2xl sm:text-[28px] text-shadow-ts">
                      UMaT - TSAP
                    </Text>
                  </MenuItem>
                </Menu>

                {/* Code for the rest of the sidebar content */}
                <div className=" pb-[10%] flex justify-center">
                  {/* Rest of the content */}
                  <div>
                    <Img
                      className="mb-[50%] h-10 w-10"
                      src="images/img_home.svg"
                      alt="home"
                    />
                    {/* Add other icons */}
                  </div>

                  {/* Show this div only in md to xl viewport */}
                  <div className="umat-logo pl-[10%] ">
                    <Text
                      className="h-10 pt-[8%] mb-[12%] flex text-lg text-white-A700"
                      size="txtPoppinsMedium18"
                    >
                      Home
                    </Text>
                    {/* Add other text items */}
                  </div>
                </div>
              </>
            )}
          </div>
        </Sidebar>
      </div>
    </>
  );
};

export default Sidebar1;
