const FrameOnePage = () => {
  const [records, setRecords] = useState([]); // Initialize state with an empty array
  const [isOpen, setIsOpen] = useState(false);

  const openNav = () => {
    setIsOpen(true);
  };
  const [isLoading, setIsLoading] = useState(true); // State to track loading status



  useEffect(() => {
    // Fetch data from the API
    fetch('https://portal.umat.edu.gh/subapi/api/Submission/GetUserSubmissions')
      .then(response => response.json())
      .then(data => {
        setRecords(data); // Update state with the fetched data
        setIsLoading(false); // Set loading status to false
        console.log(data)
      })
      .catch(error => {
        console.error('Error fetching data:', error);
        setIsLoading(false); // Set loading status to false in case of error
      });
  }, []);

  function handleFilter(event) {
    // Filter records based on the input value
    const newData = records.filter(row => {
      return row.name.toLowerCase().includes(event.target.value.toLowerCase());
    });
    setRecords(newData);
  }

  const columns = [
    // Define your table columns here
    { name: 'ID', selector: 'id', sortable: true },
    { name: 'Name', selector: 'name', sortable: true },
    { name: 'Email', selector: 'email', sortable: true },
    { name: 'Age', selector: 'age', sortable: true },
  ];

  return (
    <div className="">
      <div className="flex md:flex-col-3  h-[100%]  font-inika my-auto  w-full">
        <div className="md:relative md:col-span-1 ">
          <Sidebar1 openNav={openNav} className="h-full bg-teal-900 flex justify-start left-[0] top-[0]" />
        </div>
        <div className="  md:col-span-2 relative bg-gray-100 items-center justify-start md:ml-[0] w-full">
          <div className="">
            <div className="md:mb-[7%]  mb-[17%]">
              <StudentProfile />
            </div>
            <div className="md:pt-[5%] md:mb-[5%] md:pl-[10%] md:pr-[10%]">
              <div className="bg-white-A700 shadow-md">
                <div className="text-right mr-5 ">
                  <input type="text" className="mt-5" onChange={handleFilter} />
                </div>
                <DataTable columns={columns} data={records} selectableRows fixedHeader pagination />
              </div>
            </div>
          </div>
        </div>
      </div>
      <div className="">
        <Footer />
      </div>
    </div>
  );
};

export default FrameOnePage;









