import React, { useState } from "react";
import MainDashboardTop from "pages/Login/MainDashboardTop";
import HostelDetails from "./HostelDetails";

function HostelPages() {

    const [selectedHostel, setSelectedHostel] = useState(null);

    // Mock data for demonstration
    const hostels = [
      {
        name: "Hostel A",
        location: "City A",
        roomsAvailable: 10,
        amenities: ["Wi-Fi", "Laundry", "Parking"],
        description: "Hostel A is located in the heart of City A...",
        images: ["image1.jpg", "image2.jpg", "image3.jpg", "image4.jpg", "image5.jpg"]
      },
      // Add more hostel objects as needed
    ];
  
    const handleHostelClick = (hostel) => {
      setSelectedHostel(hostel);
    };
  


  return (
    <div>
      <MainDashboardTop />
      <div>
      {/* Render list of hostels */}
      {hostels.map((hostel, index) => (
        <div key={index} onClick={() => handleHostelClick(hostel)}>
          <h3>{hostel.name}</h3>
          {/* Render additional hostel information here if needed */}
        </div>
      ))}
      
      {/* Render HostelDetails component when a hostel is selected */}
      {selectedHostel && <HostelDetails hostel={selectedHostel} />}
    </div>
  );
}
    </div>
  );
}

export default HostelPages;
