export { Button } from "./Button";
export { Img } from "./Img";
export { Line } from "./Line";
export { Text } from "./Text";

function toggleSidebar() {
    var sidebar = document.getElementsByClassName("sidebar-content");
    var content = document.getElementById("page-content");

    if (sidebar-content.style.width === "0px" || sidebar.style.width === "") {
      content.style.width = "200px"; // Adjust this value to match the initial width of the sidebar
      content.style.marginLeft = "200px"; // Adjust this value to match the initial width of the sidebar
    } else {
      sidebar.style.width = "0px";
      content.style.marginLeft = "0px";
    }
  }
