import React, { useState } from "react";
import { Button, Img, Line, Text } from "components";

function StudentProfile() {
  const [isDropdownVisible, setIsDropdownVisible] = useState(false);

  const toggleDropdown = () => {
    setIsDropdownVisible(!isDropdownVisible);
  };

  return (
    
    <div
      className="cursor-pointer above-all   font-poppins h-full w-full"
      onClick={toggleDropdown}
    >
    <div className=""></div>
      <div className="absolute flex flex-col items-center justify-start ml-[90%] ">
        <Text
          className="mt-[30%] sm:text-[12px] md:text-[15px] text-[15px] text-blue_gray-900"
          size="txtPoppinsBold25"
        >
          Jane Doe
        </Text>

        <Text
          className="top-10 text-teal-900 text-l"
          size="txtPoppinsMedium20"
        >
          Student
        </Text>
      </div>
      <div>
        <Img
          className="absolute top-5 h-10 left-[86%] rounded-[50%] w-10"
          src="images/img_ellipse2711.png"
          alt="ellipse2711"
        />
      </div>

      {isDropdownVisible && (
        <div className="absolute top-16 right-2 bg-slate-50 rounded-md shadow-md p-2">
          <ul className="text-gray-500">
            <li>
              <a href="#">User</a>
            </li>
            <li>
              <a href="#">Settings</a>
            </li>
            <li>
              <a href="#">Logout</a>
            </li>
          </ul>
        </div>
      )}
    </div>
  );
}

export default StudentProfile;
