import React from "react";
import HostelPages from "pages/HostelPages/HostelPages";
import { useNavigate } from "react-router-dom";

const HostelComponent = ({
  image,
  name,
  location,
  price,
  amenities,
  reviews,
}) => {

  const [isOpen, setIsOpen] = useState(false);
  const navigate = useNavigate();

  const handleHostelPagesClick= () => {
    // Navigate to the new page when the button is clicked
    navigate("/HostelPages");
  };
  return (
    <div className="w-full hostel-list">
      <div className="md:flex items-center justify-center md:space-x-10 space-y-10 mb-5 md:p-10 bg-color w-[100%] ">
        <img
          src={image}
          alt={name}
          className=" hostel-img object-cover rounded-sm"
        />
        <div className=" ">
          <h2 className="bottom-headings mb-2">{name}</h2>
          <p className="text-gray-500 login2 mb-2">{location}</p>
          <div className="flex mb-3">
            <img src="images/star-icon.png" className="w-4 h-4 "></img>
            <img src="images/star-icon.png" className="w-4 h-4"></img>
            <img src="images/star-icon.png" className="w-4 h-4"></img>
          </div>
          <p className="login2 mb-2">{reviews}</p>
          <p className="bottom-headings mb-2"> â‚µ{price}</p>
      
    
      
          
            <button className="primary-color p-4 w-[60%] rounded-md text-color-white book-now" onClick={handleHostelPagesClick}>
              Book now
            </button>
         
        </div>
        <div className="hidden md:block thin-line-longer "></div>
        <ul className="mt-2 ">
          {amenities.map((amenity, index) => (
            <li key={index} className="flex items-center space-x-4  price">
              <img src="images/car-icon.png" className="w-4 h-4 "></img>
              <span className="mt-1 mb-1 w-full">{amenity}</span>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

const HostelList = () => {
  const hostels = [
    {
      id: 1,
      image: "images/hostel4.jpeg",
      name: "Hostel 1",
      location: "Hostel Location",
      reviews: "4.8/5 Wonderful (500 reviews)",
      price: 20,
      amenities: ["Free Wi-Fi", "Breakfast included", "24/7 Security"],
    },
    {
      id: 2,
      image: "images/hostel2.jpeg",
      name: "Hostel 2",
      location: "Umat Campus",
      reviews: "4.8/5 Wonderful (500 reviews)",
      price: 25,
      amenities: ["Free Parking", "Swimming Pool", "Gym"],
    },
    {
      id: 2,
      image: "images/hostel3.jpeg",
      name: "Hostel 2",
      location: "Umat Campus",
      reviews: "4.8/5 Wonderful (500 reviews)",
      price: 25,
      amenities: ["Free Parking", "Swimming Pool", "Gym"],
    },
    {
      id: 2,
      image: "images/hostel1.jpeg",
      name: "Hostel 2",
      location: "Umat Campus",
      reviews: "4.8/5 Wonderful (500 reviews)",
      price: 25,
      amenities: ["Free Parking", "Swimming Pool", "Gym"],
    },
    {
      id: 2,
      image: "images/hostel2.jpeg",
      name: "Hostel 2",
      location: "Umat Campus",
      reviews: "4.8/5 Wonderful (500 reviews)",
      price: 25,
      amenities: ["Free Parking", "Swimming Pool", "Gym"],
    },

    // Add more hostel objects as needed
  ];

  return (
    <div className="grid grid-cols-1 w-[100%] md:pl-8 pl-0">
      {hostels.map((hostel) => (
        <HostelComponent
          key={hostel.id}
          image={hostel.image}
          name={hostel.name}
          location={hostel.location}
          reviews={hostel.reviews}
          price={hostel.price}
          amenities={hostel.amenities}
        />
      ))}
    </div>
  );
};

export default HostelList;
